import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {detailsUser} from  '../actions/userActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';

export default function ProfileScreen() {

    const userSignin=useSelector(state=>state.userSignin);  // imprting user Id from react redux
    
    const {userInfo}=userSignin;
    const userDetails=useSelector((state)=>state.userSignin);
    const {loading,error,user}=userDetails;
    const dispatch=useDispatch();


    useEffect(()=>{
       dispatch(detailsUser(userInfo._id));  // detailsUser is an action in userAction
    },[dispatch,userInfo._id]);

    const submitHandler=(e)=>{
        e.preventDefault();
    };
    
    return (
        <div>
            <form className="form" onSubmit={submitHandler}>
                <div>
                    <h1>User Profile</h1>
                    <div>
                    {loading ? (
          <LoadingBox></LoadingBox>
        ) : error ? (
          <MessageBox variant="danger">{error}</MessageBox>
        ) : (
                        <>
                        <div>
                            <label htmlFor="name">Name</label>
                            <input id="name"
                            type="text" placeholder="Enter name"
                            value={user.name}></input>
                        </div>

                        <div>
                            <label htmlFor="email">
                               Email
                            </label>
                            <input id="email"
                            type="email" placeholder="Enter E-Mail"
                            value={user.email}></input>
                        </div>

                        <div>
                            <label htmlFor="password">
                                Password
                            </label>
                            <input id="password"
                            type="password" placeholder="Enter password"
                            ></input>
                        </div>

                        <div>
                            <label htmlFor="confirmPassword">
                                Confirm Password
                            </label>
                            <input id="confirmPassword"
                            type="Password" placeholder="Confirm Password"></input>
                        </div>

                        <div>
                            <label />
                            <button className="primary" type="submit">
                                Update
                            </button>
                        </div>
                        </>
                        )}
</div>
                </div>
            </form>
                    </div>
    )
}
