import React, { useState,useSelector,useEffect } from 'react';
import { Link } from 'react-router-dom'; ////name export shold be wrapped inside curly bracket
import {useDispatch} from 'react-redux';
import {signin}  from '../actions/userActions';

export default function SigninScreen() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const redirect=props.location.search?
  props.location.search.split('=')[1]:'/';
  
  const userSignin= useSelector(state => state.state)((state)=>state.userSignin);
const {userInfo}=userSignin;

  const dispatch = useDispatch();
  const submitHandler=(e)=>{
    e.preventDefault();   //on refershing the whole form willl not refresh
    dispatch(signin(email,password));
};

useEffect(()=>{
  if(userInfo){
    props.history.push(redirect);
  }
},[props.history,redirect,userInfo]);

  return (
    <div>

      <form className="form" onSubmit={submitHandler}>
        
        <div>
          <h1>Sign In</h1>
        </div>
        
        <div>
        
          <label htmlFor="email">Email address</label>
        
          <input
            type="email"
            id="email"
            placeholder="Enter email"
            required
            onChange={(e) => setEmail(e.target.value)}
          ></input>
        
        </div>
        
        <div>
        
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            placeholder="Enter password"
            required
            onChange={(e) => setPassword(e.target.value)}
          ></input>
        </div>
       
        <div>
       
          <label />
       
          <button className="primary" type="submit">
            Sign In
          </button>
        </div>
       
        <div>
          <label />
          <div>
            New customer? <Link to="/register">Create your account</Link>
          </div>
        </div>
      </form>
    </div>
  );
}
