import React, { useState } from 'react'
import CheckoutSteps from '../components/CheckoutSteps'
import { useDispatch, useSelector } from 'react-redux';
import { CART_SAVE_PAYMENT_METHOD } from '../constants/cartConstants';

export default function PaymentMethodScreen(props) {
    const cart=useSelector(state=>state.cart);
    const {shippingAddress}=cart;
    if(!shippingAddress.address){
        props.history.push('/shipping');
    }

    const [paymentMethod,setPaymentMethod]= useState('PayPal');
      const dispatch= useDispatch();
    const submitHandler=(e)=>{
        e.preventDefault();
        dispatch(CART_SAVE_PAYMENT_METHOD(paymentMethod));
        props.history.push('/placeholder');
    };
    return (
        <div>
            <CheckoutSteps step1 step2 step3></CheckoutSteps>
            <form className="form" onSubmit={submitHandler}>
                <div>
                    <h1>Payment</h1>
                </div>
                <div>
                    <div>
                        <input type="radio" id="paypal" value="PayPal"
                        name="paymentMethod" required checked onChange={(e)=>setPaymentMethod(e.target.value)}>

                        </input>

                        <label htmlFor="payal">PayPal</label>
                    </div>
                </div>

                <div>
                    <div>
                        <input type="radio" id="stripe" value="Stripe"
                        name="paymentMethod" required onChange={(e)=>setPaymentMethod(e.target.value)}>

                        </input>

                        <label htmlFor="stripe">Stripe</label>
                    </div>
                </div>
                <div>
                    <button className="primary" type="submit">
                        Continue
                    </button>
                </div>

            </form>
        </div>
    )
}
