    //delete
};

    return (
      <div className="row top">
          <div classNAme="col-2">
              <h1>Shopping Cart</h1>
              {cartItems.length===0}?<MessageBox>
                  Cart is empty.
                  <Link to = "/">Go Shopping</Link>
              </MessageBox>
              :
              (
                  <ul>
                      {
                          cartItems.map((item)=>(
                              <li key={item.product}>
                                  <div className="row">
                                      <img src={item.image}
                                       alt={item.name}
                                        className="small">
                                        </img>
</div>
<div className="min-30">
    <Link to={`/prodct/${item.product}`}>{item.name}</Link>
</div>
<div>
    <select value={item.qty} onChange=
    {e=>dispatch(addToCart(item.product),
        Number(e.target.value))}>
   {[...Array(item.countInStock).keys()].map(
                                (x) => (
                                  <option key={x + 1} value={x + 1}>
                                    {x + 1}
                                  </option>
                                )
                              )}
</select>
</div>
<div>
    ${item.price}
</div>
<div>
    <button type="button" onClick={()=>
    removeFromCartHandler(item.product)}>
        Delete
    </button>
</div>
